import Header from '../Header/Header.js';
import Main from '../Main/Main.js';
import Footer from '../Footer/Footer.js';
import PopupWithForm from '../PopupWithForm/PopupWithForm.js';
import ImagePopup from '../ImagePopup/ImagePopup.js';


function App() {

  function handleEditAvatarClick() {
    setisEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleEditProfileClick() {
    const formEdit = document.querySelector('.popup_type-edit');
    formEdit.classList.add('popup_opened');
    setisEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    const formAddCardElement = document.querySelector('.popup_type-add-card');
    formAddCardElement.classList.add('popup_opened');
  }

  const [isEditProfilePopupOpen, setisEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setisAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = useState(false);

  function closeAllPopups() {
    setisEditAvatarPopupOpen(false);
    setisEditProfilePopupOpen(false);
    setisAddPlacePopupOpen(false);
  }

  return (
    <>
      <div className="page">
        <Header />
        <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} />

        <Footer />

        <PopupWithForm title="Редактировать профиль" name="edit" isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}>
          <input className="popup__input popup__input_edit_name" id="name-input" name="name" type="text" value="" minLength="2" maxLength="40" required />
          <span className="popup__input-error" id="name-input-error"></span>
          <input className="popup__input popup__input_edit_description" id="description-input" name="about" type="text" value="" minLength="2" maxLength="200" required />
          <span className="popup__input-error" id="description-input-error"></span>
        </PopupWithForm>

        <PopupWithForm title="Обновить аватар" name="edit-avatar" isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}>
          <input className="popup__input popup__input_edit_name" id="avatar-link-input" name="avatar" type="url" value="" placeholder="Ссылка на картинку" required />
          <span className="popup__input-error" id="avatar-link-input-error"></span>
        </PopupWithForm>

        <PopupWithForm title="Новое место" name="add-card" isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
          <input className="popup__input popup__input_edit_name" id="place-name-input" name="name" type="text" value="" minLength="2" maxLength="30" placeholder="Название" required />
          <span className="popup__input-error" id="place-name-input-error"></span>
          <input className="popup__input popup__input_edit_description" id="place-link-input" name="link" type="url" value="" placeholder="Ссылка на картинку" required />
          <span className="popup__input-error" id="place-link-input-error"></span>
        </PopupWithForm>

        <PopupWithForm title="Вы уверены?" name="confirm">
        </PopupWithForm>

        <ImagePopup />
      </div>
    </>
  );
}

export default App;
