import React from 'react';
import profileAvatar from '../../images/photo-kusto.jpg';
import api from '../../utils/api.js';

function Main(props) {

    const [userName, setUserName] = React.useState("FFF");
    const [userDescription, setUserDescription] = React.useState("255, 255, 255")
    const [userAvatar, setUserAvatar] = React.useState({ profileAvatar });
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getUserInfo()
            .then((userInfo) => {
                setUserName(userInfo.name);
                setUserDescription(userInfo.about);
                setUserAvatar(userInfo.avatar);
            })
            .catch((err) => {
                console.log(err)
            });
    });

    React.useEffect(() => {
        api.getInitialCards()
            .then((cards) => {
                console.log(cards);
                setCards(cards);
            })
            .catch((err) => {
                console.log(err)
            });
    }, []);

    return (
        <body className="body">
            <div className="page">
                <main>
                    <section className="profile">
                        <div className="profile__container">
                            <div className="profile__avatar-container">
                                <img className="profile__avatar" src={userAvatar} alt="аватар" />
                                <button 
                                className="profile__edit-button profile__edit-avatar-button" 
                                type="button" 
                                aria-label="редактировать-аватар" 
                                onClick={props.onEditProfile}></button>
                            </div>
                            <div className="profile__info">
                                <div className="profile__description">
                                    <h1 className="profile__name">{userName}</h1>
                                    <p className="profile__profession">{userDescription}</p>
                                </div>
                                <button className="profile__edit-button profile__edit-description-button" 
                                type="button" 
                                aria-label="редактировать-профиль" 
                                onClick={props.onEditProfile}>
                                </button>
                            </div>
                        </div>
                        <button className="profile__add-button" 
                        type="button" 
                        aria-label="добавить-фото" 
                        onClick={props.onAddPlace}>
                        </button>
                    </section>

                    <section className="elements" aria-label="Фотографии">
                        <article className="element" key={card._id}>
                            <img className="element__photo" src={card.link} alt={card.name} />
                            <button className="element__delete" type="button" aria-label="удалить"></button>
                            <div className="element__container">
                                <h2 className="element__place-name">{card.name}</h2>
                                <div className="element__like-container">
                                    <button className="element__like" type="button" aria-label="лайк"></button>
                                    <p className="element__like-counter">{card.likes.length}</p>
                                </div>
                            </div>
                        </article>
                    </section>

                </main>
            </div>
        </body>
    )
}

export default Main;
